import React, { Component } from 'react';
import styled from 'styled-components';

const ContainerMensagem = styled.div`
    border: 1px solid red;
    height: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
`

const Usuario = styled.input`
    margin-right: 10px;
    width: 15%;
`    
const Mensagem = styled.input`
    margin-right: 10px;
    width: 70%;
`   
const BotaoEnviar = styled.button`
    margin-right: 10px;
​
`
 class Formulario extends Component {
    constructor(props) {
        super(props)
        this.state = {
            nomeusuario: "",
            msgusuario: "",
        }
    }
    saveUserName = (event) => {
        this.setState({nomeusuario: event.target.value})
    }
    saveMessage = (event) => {
        this.setState({msgusuario: event.target.value})
    }
    sendMessage = () =>{
        const mensagem = {
            nomeusuario: this.state.nomeusuario,
            msgusuario: this.state.msgusuario,
        }
        this.props.addMsg(mensagem);
        console.log(mensagem)

    }
  render() {
    return (
      <ContainerMensagem>
​
        <Usuario 
        type="text" 
        placeholder="Usuário"
        onChange={this.saveUserName} 
        value={this.state.nomeusuario}
        />
​
        <Mensagem 
        type="text" 
        placeholder="Mensagem" 
        onChange={this.saveMessage}
        value={this.state.msgusuario}
        />
​
        <BotaoEnviar
        onClick={this.sendMessage}>
            Enviar
        </BotaoEnviar>
​
      </ContainerMensagem>
    );
  }
}

export default Formulario;
