import React, { Component } from 'react';
import { render } from 'react-dom';
import Formulario from './components/Formulario'
import styled from 'styled-components';

const Principal = styled.div`
  height: 90vh;
  width: 800px;
  border: 1px solid black;
  margin-top: 10px;
`
const CaixaDeMensagens = styled.div`
  height: 90vh;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
`
const MensagemIndividual = styled.li`
  list-style: none;
`
const TextoNegrito = styled.span`
  font-weight: bold;
`
const Wrapper = styled.div`
  display: flex;
  justify-content: center;
`
class App extends Component {
  constructor() {
    super()
    this.state = {
      mensagens: [],
    }
  }

  addMsg = (mensagem) => {
    this.setState(
      { mensagens: [...this.state.mensagens, mensagem] }
    )
  }

  render() {
    const listaDeMensagens = this.state.mensagens.map((mensagem, index) => {
      return <MensagemIndividual key={index}><TextoNegrito>{mensagem.nomeusuario}</TextoNegrito>{" : "}{mensagem.msgusuario}</MensagemIndividual>
    })

    return (

      <Wrapper>
        <Principal>
​
          <CaixaDeMensagens>
            <ul>
              {listaDeMensagens}
            </ul>
          </CaixaDeMensagens>
​
          <Formulario addMsg={this.addMsg} />
​
​
        </Principal>
      </Wrapper>
    );
  }
}


render(<App />, document.getElementById('root'));

export default App;
