import React, { Component } from 'react';
import { render } from 'react-dom';
import styled from 'styled-components';

const CadaPost = styled.li`
  color: blue;
`
class App extends Component {
  // constructor() {
  //   super();
  //   this.state = {
  //     listaDePost: [{
  //       fotoUsuario: ' ',
  //       nomeUsuario: ' ',
  //       urlImg: ' '
  //     }];
  //   }
  // }

  criarPost = () => {
    console.log('Adicionando item: ', this.state.nomeUsuario, this.state.fotoUsuario, this.state.urlImg)

    const novoPost = {
      fotoUsuario: this.state.fotoUsuario,
      nomeUsuario: this.state.nomeUsuario,
      urlImg: this.state.urlImg
    }

    const copiaListaDePost = [novoPost, ...this.state.listaDePost]

    this.setState({
      listaDePost: copiaListaDePost,
      fotoUsuario: ' ',
      nomeUsuario: ' ',
      urlImg: ' '
    })
  }

  onChangeFotoUsuario = (event) => {
    this.setState({fotoUsuario: event.target.value})
  }

  onChangeNomeUsuario = (event) => {
    this.setState({nomeUsuario: event.target.value})
  }

  onChangeUrlImg = (event) => {
    this.setState({fotoUsuario: event.target.value})
  }

  render() {

    const listaPost = this.state.listaDePost.map((post, index) => {
      return <CadaPost key={index} > {post.fotoUsuario + post.nomeUsuario + post.urlImg} 
      </CadaPost>
    })

    return (
      <div>
          <input
            type="text" 
            placeholder="Foto do Usuário"
            value= {this.state.fotoUsuario}
            onChange={this.onChangeFotoUsuario}
          />
          <input 
            type="text" 
            placeholder="Nome do Usuário"
            value= {this.state.nomeUsuario}
            onChange={this.onChangeNomeUsuario}
          />
          <input 
            type="text" 
            placeholder="URL do Usuário"
            value= {this.state.urlImg}
            onChange={this.onChangeUrlImg}
          />
          <button onClick={this.criarPost}>Enviar</button>

          <ul>
            {listaPost}
          </ul>
      </div>
    );
  }
}

render(<App />, document.getElementById('root'));

export default App;
