import React, { Component } from 'react';
import { render } from 'react-dom';
import styled from 'styled-components';

const ContainerFormulario = styled.div` 
  display:flex;
  justify-content: center;
`

const Formulario = styled.div`
  display:flex;
  flex-direction: column;
  width: 300px;
  height: 200px;
  align-items: center;
  border: 1px solid black;
  justify-content: center;

`
const Button = styled.button`
  width: 50px;
  margin-top: 4px;
`

const Input = styled.input `
  margin: 3px;
  width: 200px;
`

class App extends Component {

  constructor() {
    super();
    this.state = {
      valorNome: ' ',
      valorImage: ' ',
      valorUrl: ' ',
      listaDePost:[]
    }
  }

  criarPost = () => {

    const novoPost = {
      fotoUsuario: this.state.valorImage,
      nomeUsuario: this.state.valorNome,
      urlImg: this.state.valorUrl
    }

    const copiaListaDePost = [novoPost, ...this.state.listaDePost]

    this.setState({
      listaDePost: copiaListaDePost,
      valorNome: ' ',
      valorImage: ' ',
      valorUrl: ' '
    })
  }

  onChangeFotoUsuario = (event) => {
    this.setState({valorImage: event.target.value})
  }

  onChangeNomeUsuario = (event) => {
    this.setState({valorNome: event.target.value})
  }

  onChangeUrlImg = (event) => {
    this.setState({valorUrl: event.target.value})
  }

  render() {
    const listaTodosPost = this.state.listaDePost.map((post, index) => {
      return <li key={index} > {post.valorNome + post.valorImage + post.valorUrl} 
      </li>
    })

    return (
      <ContainerFormulario>
        <Formulario>
            <h3>Criar um novo post</h3>
            <Input
              type="text" 
              placeholder="Foto do Usuário"
            />
            <Input 
              type="text" 
              placeholder="Nome do Usuário"
            />
            <Input 
              type="text" 
              placeholder="URL do Usuário"
            />

            <Button onClick="criarPost">
              Enviar
            </Button>
        </Formulario>
        <ul>
          {listaTodos}
        </ul>

      </ContainerFormulario>
    );
  }
}

render(<App />, document.getElementById('root'));

export default App;
