import React, { Component } from 'react';
import { render } from 'react-dom';
import './style.css';
import styled from 'styled-components'

const ItemDaLista = styled.li`
  color: ${props => props.cor};
`

class App extends Component {
  constructor() {
    super();
    this.state = {
      listaDeItens: [{
        texto: 'Blablabla',
        cor: '#ff0000'
      },
      {
        texto: 'Bleus',
        cor: '#00ff00'
      }],
      valorTexto: '',
      valorCor: ''
    };
  }

  adicionarItem = () => {
    console.log('Adicionando item: ', this.state.valorTexto, this.state.valorCor)
    const novoItem = {
      texto: this.state.valorTexto,
      cor: this.state.valorCor
    }

    const copiaListaDeItens = [novoItem, ...this.state.listaDeItens]

    this.setState({
      listaDeItens: copiaListaDeItens,
      valorTexto: '',
      valorCor: ''
    })
  }

  onChangeTexto = (event) => {
    this.setState({valorTexto: event.target.value})
  }

  onChangeCor = (event) => {
    this.setState({valorCor: event.target.value})
  }

  render() {
    const listaDeElementos = this.state.listaDeItens.map((item, index) => {
      return <ItemDaLista key={index} cor={item.cor}>{item.texto + ' ' + item.cor}</ItemDaLista>
    })

    return (
      <div>
        <input 
          type="text" 
          placeholder="Texto do item"
          value={this.state.valorTexto}
          onChange={this.onChangeTexto}
        />
        <input 
          type="color"
          value={this.state.valorCor}
          onChange={this.onChangeCor}
         />
        <button onClick={this.adicionarItem}>Adicionar Item</button>
        <ul>
          {listaDeElementos}
        </ul>
      </div>
    );
  }
}

render(<App />, document.getElementById('root'));
